import React, {Component} from 'react'; 
import styled, { css } from 'styled-components'

const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    margin-top: 2em;
    margin-bottom: 2em;
    margin-left: 20%;
    margin-right: 20%;

    @media (max-width: 500px) {
      flex-direction: column;
      margin-right: 2em;
      margin-left: 2em;

    }

    @media (max-width: 800px) {
        flex-direction: column;
    }
`;

const CaseTitle = styled.div`

    font-weight: normal;
    font-family: Overpass;
    font-size: 3rem;
    line-height: 1.4;
    font-weight: 600;
    margin-bottom: 1em;
    width: 10em;

    @media (max-width: 1200px) {
       font-size: 35px;
       }
`;

const Text = styled.div`
    color: #3a3a3a;
    font-family: Open Sans;
    font-weight: 400;
    font-size: 18px;
    margin-bottom: 1em;
    padding-right: 2em;
    line-height: 2;

    @media (max-width:1200px) {
      padding-right: 0em;
      font-size: 16px;

      width: 100%;
     }
`;

const TextWrapper = styled.div`
     display: flex;
     flex-direction: column;
    padding-top: 5em;
    padding-bottom: 5em;
    padding-right: 5%;
    width: 30em;


    @media (max-width: 800px) {
     padding-bottom: 2em;
     padding-top: 4em;
     padding-right: 0em;
     width: 100%;

    }
`;

const ImageWrapper = styled.div`

margin-right: 20%;
flex-shrink: 0;
`;

const Feature = ( {text, color} ) => {
    return(
        <Wrapper> 
        <TextWrapper>
          <CaseTitle > I’m interested in community.  </CaseTitle>
          <Text> I’m a designer, entrepreneur, and junior at Stanford who cares about people-powered action.  It’s why I built Peerlift, a startup that’s helped 60,000 students find scholarships for college.   And it’s why I spent time in Paris this year as product designer #1 at an early-stage French startup.  New language, new culture, same mission. </Text>
        </TextWrapper>
        <ImageWrapper> <img className = "about-img" src ={sam} />   </ImageWrapper>
      </Wrapper>
    )
  }

  export default Feature;
