import React, {Component} from 'react'; 
import styled, { css } from 'styled-components'
import './about.css';
import Title from "./components/title";



//MQ here at 500px 
const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    margin-right: 20%;
    margin-left: 20%;
    margin-top: 2em;
    margin-bottom: 2em;

    @media (max-width: 500px) {
      flex-direction: column;
      margin-right: 2em;
      margin-left: 2em;

    }

    @media (max-width: 800px) {
        flex-direction: column;
    }
`;

const CaseTitle = styled.div`
    font-weight: normal;
    font-family: Overpass;
    font-size: 28px;
    line-height: 1.4;
    font-weight: 600;
    margin-bottom: 1em;

`;

const Text = styled.div`
    color: #3a3a3a;
    font-family: Open Sans;
    font-weight: 800;
    font-size: 22px;
    margin-bottom: 1em;
    padding-right: 2em;
    @media (max-width: 800px) {
      padding-right: 0em;

     }


`;

const TextWrapper = styled.div`
     display: flex;
     flex-direction: column;
    padding-top: 5em;
    padding-bottom: 5em;

    @media (max-width: 800px) {
     padding-bottom: 2em;
     padding-top: 4em;
     padding-right: 0em;

    }
`;

const About = ( ) => {
  return(
    <Wrapper> 
      <TextWrapper>
        <CaseTitle text = "I’m interested in community." > {header}  </CaseTitle>
        <Text textOne = "I’m a designer, entrepreneur, and junior at Stanford who cares about people-powered action.  It’s why I built Peerlift, a startup that’s helped 60,000 students find scholarships for college.   And it’s why I spent time in Paris this year as product designer #1 at an early-stage French startup.  New language, new culture, same mission."
        >  </Text>
        <Text textTwo = {textTwo} > {textTwo} </Text>
      </TextWrapper>
      <img className = "split-img" src ={require(`${image}`)} /> 
    </Wrapper>
  )
}

export default About;
